clear;$folderPath=Read-Host "Type folder path to organise ";$subFolders= Get-ChildItem -Recurse -Path $folderPath -Directory ;foreach($subFolder in $subFolders){$files = Get-ChildItem -Path $subFolder.FullName -File;foreach($file in $files){Move-Item -Path $file.FullName -Destination $folderPath}};$extToSubfolder = @{".jpg"="Images";".jpeg"="Images";".png"="Images";".heic"="Images";".heif"="Images";".webp"="Images";".tiff"="Images";".eps"="Images";".svg"="Images";".ai"="Images";".pdf"="PDF";".gif"="GIF";".ps1"="Code";".py"="Code";".java"="Code";".js"="Code";".cs"="Code";".c"="Code";".h"="Code";".cpp"="Code";".hpp "="Code";".php"="Code";".ts"="Code";".r"="Code";".html"="Code";".rb"="Code";".go"="Code";".vba"="Code";".vbs"="Code";".rs"="Code";".lua"="Code";".m"="Code";".txt"="Text";".csv"="Text";".docx"="MS Word";".doc"="MS Word";".xlsx"="MS Excel";".xls"="MS Excel";".xlss"="MS Excel";".xlsm"="MS Excel";".xlsb"="MS Excel";".xla"="MS Excel add-on files";".pptx"="MS Powerpoint";".ppt"="MS Powerpoint";".ppsx"="MS Powerpoint";".mp3"="Audio";".wav"="Audio";".mp4"="Video";".avi"="Video";".webm"="Video";".zip"="Compressed";".rar"="Compressed";".gz"="Compressed";".exe"="Executable";".dll"="Executable";".dng"="Raw Photos"};foreach($subfolder in $extToSubfolder.Values | Sort-Object -Unique){;$subFolderPath = Join-Path -Path $folderPath -ChildPath $subfolder;if (-not (Test-Path -Path $subFolderPath)){New-Item -Path $subFolderPath -ItemType Directory;}};Get-ChildItem -Path $folderPath -File | Move-Item -Destination {Join-Path -Path $folderPath -ChildPath $extToSubfolder[$_.Extension] };Get-ChildItem -Path $folderPath -Directory | Where-Object {(Get-ChildItem -Path $_.FullName -File).Count -eq 0 } | Remove-Item;clear;Write "Done Organising"